<?php	/* successfull or unsuccessfull campaign status maintain block srarts */	function my_plugin_function(){		$tabs = array( 'successfull-campaign' => 'Successfull Campaign', 'unsuccessfull-campaign' => 'Unsuccessfull Campaign' );		echo '<div id="icon-themes" class="icon32"><br></div>';		echo '<h2 class="nav-tab-wrapper">';				if ( isset ( $_GET['tab'] ) ){ 			$ptab = $_GET['tab'];		}		else{			$ptab = 'successfull-campaign';		}		foreach( $tabs as $tab => $name ){			$class = ( $tab == $ptab ) ? ' nav-tab-active' : '';			echo "<a class='nav-tab$class' href='?page=teecircle-settings&tab=$tab'>$name</a>";		}		echo '</h2>';						if ( isset ( $_GET['tab'] ) ) 			$tab = $_GET['tab'];					else $tab = 'successfull-campaign';		echo '<table class="form-table">';		switch ( $tab ){			case 'successfull-campaign' :				?>				<tr>					<th>Event</th>					<th>Title</th>					<th>Status</th>				</tr>								<?php				if(!empty($success_list) && count($success_list)){					foreach($success_list AS $key=>$val){						?>						<tr>							<td><?php  ?></td>							<td><?php  ?></td>							<td><?php  ?></td>						</tr>						<?php 					}				}				else{					?><tr><td class="no-data">There is no successfull campaigns</td></tr><?php 				}			break;			case 'unsuccessfull-campaign' :				?>								<tr>					<th>Event</th>					<th>Title</th>					<th>Status</th>				</tr>				<?php				if(!empty($unsuccess_list) && count($unsuccess_list)){					foreach($unsuccess_list AS $key=>$val){						?>						<tr>							<td><?php  ?></td>							<td><?php  ?></td>							<td><?php  ?></td>						</tr>						<?php 					}				}				else{					?><tr><td class="no-data">There is no unsuccessfull campaigns</td></tr><?php 				}			break;			default:			 			break;		}		echo '</table>';		?>		<!-- 		<p class="submit" style="clear: both;">			<input type="submit" name="Submit"  class="button-primary" value="Update Settings" />			<input type="hidden" name="ilc-settings-submit" value="Y" />		</p>		-->		<?php 	}	/* successfull or unsuccessfull campaign status maintain block ends */